        logging.warning("NEWS_API_KEY not found in environment variables, using demo data")
        return get_demo_news(symbol, limit)

    url = "https://newsapi.org/v2/everything"
    params = {
        'q': f'"{symbol}" OR "{get_company_name(symbol)}"',
        'language': 'en',
        'sortBy': 'publishedAt',
        'pageSize': limit,
        'apiKey': api_key
    }

