}

.shadow-sm {
    box-shadow: 0 0.125rem 0.25rem rgba(0, 0, 0, 0.075) !important;
}

.shadow {
    box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15) !important;
}

.border-radius {
    border-radius: var(--border-radius) !important;
}

.spacing-sm {
    padding: var(--spacing-sm);
}

.spacing-md {
    padding: var(--spacing-md);
}

